import React from 'react';

function RoomVisualizer({ roomData }) {
    // Assuming roomData contains dimensions in inches
    // Assuming roomData contains dimensions in inches
    const { dimensions, doorPlacement, windowPlacement } = roomData;

    // Define the scale factor (1 inch = 10 SVG units)
    const scaleFactor = 5;

    // Convert inch measurements to SVG units
    const roomWidth = dimensions.width * scaleFactor;
    const roomLength = dimensions.length * scaleFactor;

    // Calculate the width of the door proportionate to the room width
    const doorWidth = roomWidth * 0.05; // Adjust as needed

    // Calculate the x-coordinate to center the door within the room
    const doorX = 0;

    // Adjust the y-coordinate for the door placement as needed
    const doorY = doorPlacement.y * scaleFactor;

    // Convert window coordinates
    const windowCoordinates = windowPlacement.map(window => ({
        x: window.x * scaleFactor,
        y: window.y * scaleFactor
    }));

    // Assuming the door height is 80 inches, adjust as needed
    const doorHeight = 32 * scaleFactor;

    return (
        <svg width={roomWidth} height={roomLength}>
            {/* Draw room walls */}
            <rect x="0" y="0" width={roomWidth} height={roomLength} fill="lightgrey" />

            {/* Draw door */}
            <rect x={doorX} y={doorY} width={doorWidth} height={doorHeight} fill="brown" />

            {/* Draw windows */}
            {windowCoordinates.map((window, index) => (
                <rect key={index} x={window.x} y={window.y} width="10" height="10" fill="blue" />
            ))}
        </svg>
    );


}

export default RoomVisualizer;
