import React, { useEffect } from 'react';

function BackgroundMusic() {
    useEffect(() => {
        const audioElement = document.getElementById('background-music');

        // Add event listener for when the audio starts playing
        audioElement.addEventListener('play', () => {
            console.log('Background music is playing...');
        });

        // Add event listener for when the audio finishes playing
        audioElement.addEventListener('ended', () => {
            console.log('Background music has ended.');
        });

        return () => {
            // Clean up event listeners when the component unmounts
            audioElement.removeEventListener('play');
            audioElement.removeEventListener('ended');
        };
    }, []); // Empty dependency array ensures effect runs only once after initial render

    return (
        <audio id="background-music" controls autoPlay loop>
            <source src="/backgroundMusic.mp3" type="audio/mpeg" />
            Your browser does not support the audio element.
        </audio>
    );
}

export default BackgroundMusic;
