import React from 'react';

function RoomVisualizer({ roomData }) {
    // Assuming roomData contains dimensions in inches
    const { width, length, doorPosition, windowWall, windowPosition } = roomData;

    // Define the scale factor (1 inch = 10 SVG units)
    const scaleFactor = 5;

    // Door and window widths
    const doorWidthInches = 32;
    const windowWidthInches = 22;

    // Convert inch measurements to SVG units
    const roomWidth = width * scaleFactor;
    const roomLength = length * scaleFactor;

    // Calculate the width and height of the door and window proportionate to the room
    const doorWidth = doorWidthInches * scaleFactor;
    const doorHeight = 32 * scaleFactor; // Assuming door height is 80 inches, adjust as needed
    const windowHeight = 2; // Height of the window line

    return (
        <svg width={roomWidth} height={roomLength}>
            {/* Draw room walls */}
            <rect x="0" y="0" width={roomWidth} height={roomLength} fill="lightgrey" />

            {/* Draw door */}
            <rect x="0" y={doorPosition * scaleFactor} width={doorWidth} height={doorWidth} fill="brown" />

            {/* Draw windows */}
            {windowWall === 'top' && (
                <rect x={windowPosition * scaleFactor} y="0" width={windowWidthInches * scaleFactor} height={windowHeight} fill="blue" />
            )}
            {windowWall === 'bottom' && (
                <rect x={windowPosition * scaleFactor} y={roomLength - windowHeight} width={windowWidthInches * scaleFactor} height={windowHeight} fill="blue" />
            )}
            {windowWall === 'left' && (
                <rect x="0" y={windowPosition * scaleFactor} width={windowWidthInches * scaleFactor} height={windowHeight} fill="blue" />
            )}
            {windowWall === 'right' && (
                <rect x={roomWidth - (windowWidthInches * scaleFactor)} y={windowPosition * scaleFactor} width={windowWidthInches * scaleFactor} height={windowHeight} fill="blue" />
            )}
        </svg>
    );
}

export default RoomVisualizer;
